    </div>
    <div class="relative">
      <vue3-slider 
        @change="updateSlider" 
        disabled="disabled" 
        v-model="value" 
        :tooltip="true" 
        :min="config.min" 
        :max="config.max" 
        :step="100"
        class="pt-5 budget-slider"
        :class="[disabled ? 'handle-disabled' : '']"
      />
      <div class="vertical-slide"/>   
      <div class="w-full flex flex-row justify-between mt-5">
        <div class="flex text-xs tracking-wide leading-tight text-green-900"  v-html="config.label_min"></div>
        <div class="flex text-xs tracking-wide leading-tight text-green-900"  v-html="config.label_max"></div>
      </div>
      <div class="w-full flex flex-row justify-between mt-4">
        <InputInteger 
          @change="updateInput" 
          v-model="modelValue" 
          :config="{ min: config.min, max: config.max, label: 'Vul het exacte bedrag in als je hierover beschikt', id: 'inoutbudget', placeholder: '' }"
        /> 
      </div>
    </div>
  </div>
</template>

<script>

import slider from "vue3-slider"
import InputInteger from './InputInteger.vue'


export default {
  name: "SliderBudget",
  props: {
    config: {
      type: Object,
      default: "",
    },
    modelValue: {
      type: [String, Number],
      default: 100,
    },
  },
  components: {
    "vue3-slider": slider,
    InputInteger,
  },
  data() {
      return {
          value: 100,
          disabled: false,
      };
  },
  mounted() {
      this.value = this.modelValue;
  },
  methods: {
    updateSlider(value) {
        this.disabled = false;
        this.$emit("update:modelValue", value);
    },
    updateInput(event) {
        this.disabled = true;
        this.$emit("update:modelValue", event.target.value);
    }
  }
};

</script>

